(function(){$(document).ready(function(){var answers;answers=$(".survey_section .pick-one > ol");$("input[type = submit]").bind("click",function(e){var invalid;invalid=[];answers.each(function(){var fieldset;fieldset=$(this).parents("fieldset.pick-one");if($(this).find("input[type='radio']:checked").size()===0){invalid.push(fieldset);return fieldset.setCssState("invalid")}else{return fieldset.setCssState("valid")}});if(_.isEmpty(invalid)!==true){return false}});return $(".survey_section").delegate(".state-invalid input[type=radio]","click",function(e){return $(this).parents("fieldset.pick-one").setCssState("valid")})})}).call(this);